import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-contacto',
  templateUrl: './contacto.component.html',
  styleUrls: ['./contacto.component.css'],
})
export class ContactoComponent {
  nombre: = '';
  correo: string = '';
  mensaje: string = '';

  constructor(private http: HttpClient) {}

  enviarFormulario() {
    console.log('Nombre:', this.nombre);
    console.log('Correo:', this.correo);
    console.log('Mensaje:', this.mensaje);

    const data = {
      nombre: this.nombre,
      correo: this.correo,
      mensaje: this.mensaje,
    };

    console.log('Enviando formulario', data);

    /*this.http.post('http://localhost:3000/enviar-correo', data)
      .subscribe(response => {
        console.log('Formulario enviado con éxito');
        // Realiza cualquier otra acción necesaria después de enviar el correo.
      }, error => {
        console.error('Error al enviar el formulario', error);
      });*/
  }
}
<div class="contacto">
  <h2>Contacto</h2>
  <form (submit)="enviarFormulario()">
    <div class="campo">
      <label for="nombre">Nombre</label>
      <input id="nombre" type="text" required [(ngModel)]="nombre">
    </div>
    
    <div class="campo">
      <label for="correo">Correo Electrónico</label>
      <input id="correo" type="email" required [(ngModel)]="correo">
    </div>
    
    <div class="campo">
      <label for="mensaje">Mensaje</label>
      <textarea id="mensaje" required [(ngModel)]="mensaje"></textarea>
    </div>
    <div class="campo">
      <button type="submit">Enviar</button>
    </div>
  </form>
</div>
